import 'package:flutter/material.dart';
import 'package:tody_app/core/app_colors.dart';
import 'package:tody_app/core/utils/extension/theme.ext.dart';

class StaticCategoryItem extends StatelessWidget {
  const StaticCategoryItem({
    super.key,
    required this.prefix,
    required this.title,
    this.onTap,
  });

  final Widget prefix;
  final String title;
  final VoidCallback? onTap;

  @override
  Widget build(BuildContext context) {
    return InkWell(
      onTap: onTap,
      child: Padding(
        padding: const EdgeInsets.only(
          left: 20,
          right: 15,
          top: 8,
          bottom: 8,
        ),
        child: Row(
          children: [
            Expanded(
              child: Row(
                children: [
                  prefix,
                  const SizedBox(width: 16),
                  Expanded(
                    child: Text(
                      title,
                      style: Theme.of(context).textTheme.bodyLarge,
                    ),
                  ),
                ],
              ),
            ),
            Icon(
              Icons.chevron_right,
              color: Theme.of(context).select(
                light: AppColors.onSurfaceMediumBrush,
                dark: DarkAppColors.onSurfaceMediumBrush,
              ),
            ),
          ],
        ),
      ),
    );
  }
}
